import React, { useContext } from "react";

import { addToCart } from "../helpers/addToCartHelper";
import { OrderContext } from "../contexts/OrderContext";

export default function AddToCartButton({ productId, handleOrderContext }) {
  return (
    <button
      id={productId}
      onClick={(e) => {
        console.log(e);
        addToCart(e.target.id, handleOrderContext);
      }}
    >
      Add To Cart
    </button>
  );
}
