import React from "react";
import { useParams } from "react-router-dom";
import useApiFetch from "../hooks/useApiFetch";

export default function ProductPage() {
  const { productSlug } = useParams();
  const url = `http://localhost:5000/api/products/${productSlug}`;

  const [result, loading] = useApiFetch(url);
  console.log(result.product);
  return (
    <div className="product-container">
      <div className="product-images">
        <img
          src={`http://localhost:5000/api/productImages/${
            result.product.ProductImages[0].path.split("/")[3]
          }`}
          alt=""
        />
      </div>
      <div className="product-info">
        <img
          src={`http://localhost:5000/api/productImages/${
            result.product.ProductImages[1].path.split("/")[3]
          }`}
          alt=""
        />
      </div>
    </div>
  );
}
