import React, { useContext, useEffect, useState } from "react";
import { getCookie } from "../helpers/cookies";
import AddToCartButton from "../components/AddToCartButton";
import { OrderContext, OrderContextProvider } from "../contexts/OrderContext";

export default function CartPage() {
  const [order, setOrder] = useState(null);
  const { orderContext, handleOrderContext } = useContext(OrderContext);

  useEffect(() => {
    const orderCookie = getCookie("order");
    setOrder(orderCookie);
  }, []);

  return order ? (
    <div>
      {orderContext.order_total}

      <AddToCartButton
        handleOrderContext={handleOrderContext}
        productId={258}
      />
    </div>
  ) : (
    <p>loading</p>
  );
}
