import React, { useEffect, useState } from "react";
import useApiFetch from "../hooks/useApiFetch";

export default function ProductList({ categorySlug }) {
  const url = categorySlug
    ? `http://localhost:5000/api/category/alle-items-in-de-sale`
    : "";
  console.log(url);
  const [products, loading] = useApiFetch(url);
  console.log(products.products);
  const productList = products.products.map((product) => {
    console.log(product.ProductImages[0].path.split("/")[3], "ey");
    return (
      <li className="product-container">
        <img
          src={`localhost:5000/api/productImages/${
            product.ProductImages[0].path.split("/")[2]
          }`}
          alt={product.slug}
        />
        <span>{product.name}</span>
        <span>{product.price}</span>
      </li>
    );
  });
  return <>{loading ? <h1>loading</h1> : <ul>{productList}</ul>}</>;
}
