  //     street_name: formData.get("street_name"),
  //     house_number: formData.get("house_number"),
  //     floor: formData.get("floor"),
  //     country: formData.get("country"),
  //   };
  const reqBody = { guestFirstName, guestLastName, guestEmail, address };

  const fetchUrl = `http://localhost:5000/api/checkout`;

  if (!guestCustomerId) {
    guestCustomerId = Date.now();
    setCookie("guestCustomerId", guestCustomerId);
  }

  const options = {
    method: "POST",
    headers: {
      "Content-Type": "application/json", // Set content type to JSON
    },
    body: JSON.stringify(reqBody),
  };
  fetchApi(fetchUrl, options)
    .then((res) => {
      console.log(res.body);
      //setCookie("order", JSON.stringify(res.order));
    })
    .catch((err) => console.log(err));
}
