import React, { useContext } from "react";

import { OrderContext } from "../contexts/OrderContext";
import UpdateCartButton from "../components/UpdateCartButton";

export default function CartPage() {
  const { orderContext, handleOrderContext } = useContext(OrderContext);
  const cartItems = orderContext.Products.map((product) => {
    return (
      <div className="cart-item">
        <h2>{product.name}</h2>
        <img
          className="product-detail-image"
          src={`http://localhost:5000/api/productImages/${
            product.ProductImages[0].path.split("/")[3]
          }`}
          alt={product.name}
        />
        <p>product.price</p>
        <UpdateCartButton
          handleOrderContext={handleOrderContext}
          productId={258}
          action="remove"
          textContent="-"
        />
        <span>{product.OrderDetails.quantity}</span>
        <UpdateCartButton
          handleOrderContext={handleOrderContext}
          productId={258}
          action="add"
          textContent="+"
        />
      </div>
    );
  });
  return orderContext ? (
    <div className="cart-items">{orderContext.order_total}</div>
  ) : (
    <p>loading</p>
  );
}
