import { getCookie, setCookie } from "./cookies";
import { fetchApi } from "./fetchHelper";

export function addToCart(productId) {
  let guestCustomerId = getCookie("guestCustomerId");

  if (!guestCustomerId) {
    guestCustomerId = Date.now();
    setCookie("guestCustomerId", guestCustomerId);
  }

  let formBody = new FormData();
  formBody.append("productId", parseInt(productId));
  formBody.append("quantity", 1);
  formBody.append("guestCustomerId", guestCustomerId);
  console.log(formBody.get("quantity"));
  const options = { method: "POST", body: formBody };
  fetchApi("http://localhost:5000/api/cart/add", options).then((res) => {
    console.log(res);
    setCookie("order", res.order);
  });
}
