import React from "react";

import { addToCart } from "../helpers/addToCartHelper";

export default function AddToCartButton({ productId }) {
  return (
    <button
      id={productId}
      onClick={(e) => {
        console.log(e);
        addToCart(e.target.id);
      }}
    >
      Add To Cart
    </button>
  );
}
