import React, { useContext, useEffect, useState } from "react";
import { deleteCookie, getCookie, setCookie } from "../helpers/cookiesHelpers";
import { OrderContext } from "../contexts/OrderContext";
import { fetchApi } from "../helpers/fetchHelper";

export default function PaymentReturnPage() {
  const { orderContext, handleOrderContext } = useContext(OrderContext);
  const [orderStatus, setOrderStatus] = useState("pending");
  console.log(orderContext.id);

  useEffect(() => {
    const intervalId = setInterval(() => {
      fetchApi(
        `http://localhost:5000/api/checkout/status/${orderContext.id}`
      ).then((res) => {
        console.log(res.order.order_status);
        if (res.order_status === "closed") {
          setOrderStatus(res.order.order_status);
          deleteCookie("order");
          clearInterval(intervalId);
        }
      });
    }, 5000);
  }, []);

  return <div>{orderStatus}</div>;
}
