import React from "react";

import { addToCart } from "../helpers/addToCartHelper";

export default function AddToCartButton({
  productId,
  handleOrderContext,
  action,
  textContent,
}) {
  const button = (
    <button
      id={productId}
      onClick={(e) => {
        addToCart(e.target.id, handleOrderContext, action);
      }}
    >
      {textContent}
    </button>
  );
  return button;
}
