import { getCookie, setCookie } from "./cookies";
import { fetchApi } from "./fetchHelper";

export function addToCart(productId) {
  let guestCustomerId = getCookie("guestCustomerId");

  if (!guestCustomerId) {
    guestCustomerId = Date.now();
    setCookie("guestCustomerId", guestCustomerId);
  }
  // const body = {
  //   productId: productId,
  //   quantity: 1,
  //   guestCustomerId: guestCustomerId,
  // };

  let formData = new FormData();
  formData.append("productId", parseInt(productId));
  formData.append("quantity", 1);
  formData.append("guestCustomerId", guestCustomerId);
  console.log(formData.get("quantity"));

  const options = { method: "POST", body: formData };
  fetchApi("http://localhost:5000/api/cart/add", options)
    .then((res) => {
      console.log(res);
      setCookie("order", res.order);
    })
    .catch((err) => console.log(err));
}
